
public class Pattern {

	public static void print()
	{
		int i=0,j;
		for(;i<4;i++)
		{
			for(j=0;j<4;j++)
			{
				if(i>=j)
				{
					System.out.print("*");
				}
					
		}
			System.out.print("\n");
	}
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
print();
	}

}
